import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import api from '../services/api';

const EditEmployee = () => {
  const [employee, setEmployee] = useState({
    name: '',
    email: '',
    department: '',
    salary: ''
  });
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { name } = useParams();

  useEffect(() => {
    fetchEmployee();
  }, [name]);

  const fetchEmployee = async () => {
    try {
      const response = await api.get(`/employees/${name}`);
      setEmployee(response.data);
    } catch (error) {
      setError('Failed to fetch employee details');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.put(`/employees/${name}`, employee);
      navigate('/');
    } catch (error) {
      setError('Failed to update employee');
    }
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="card">
            <div className="flex justify-between items-center mb-6">
              <h1>Employee Directory</h1>
              <Link to="/add" className="btn-primary1">Add New Employee</Link>
            </div>
            <div className="max-w-2xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
              <div className="card">
                <h1 className="mb-6">Add New Employee</h1>
                <form onSubmit={handleSubmit} className="space-y-6">
                  <div className="form-group">
                    <label className="form-label">Name</label>
                    <input
                      type="text"
                      className="input-field"
                      value={employee.name}
                      onChange={(e) => setEmployee({ ...employee, name: e.target.value })}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label className="form-label">Email</label>
                    <input
                      type="email"
                      className="input-field"
                      value={employee.email}
                      onChange={(e) => setEmployee({ ...employee, email: e.target.value })}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label className="form-label">Department</label>
                    <input
                      type="text"
                      className="input-field"
                      value={employee.department}
                      onChange={(e) => setEmployee({ ...employee, department: e.target.value })}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label className="form-label">Salary</label>
                    <input
                      type="number"
                      className="input-field"
                      value={employee.salary}
                      onChange={(e) => setEmployee({ ...employee, salary: e.target.value })}
                      required
                    />
                  </div>
                  {error && (
                    <div className="bg-red-50 border-l-4 border-red-500 p-4 mb-4">
                      <p className="text-red-700">{error}</p>
                    </div>
                  )}
                  <div className="flex justify-end space-x-4">
                    <button type="submit" className="btn-primary">Save Employee</button>
                  </div>
                </form>
              </div>
            </div>
          </div>
      </div>    
  );
};

export default EditEmployee;
